import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate, useParams } from "react-router-dom";
import Button from "../../components/Button";
import TextField from "../../components/TextField";
import { viewCard } from "./userSlice";

const ViewCard = () => {
  const params = useParams();
  const dispatch = useDispatch();
  const users = useSelector((store) => store.users);
  console.log(users);
  const navigate = useNavigate();
  const existingUser = users.filter((user) => user.id === params.id);

  console.log(existingUser[0]);
  const { name, phone, addr, SNS, position, email, time, memo } =
    existingUser[0];
  const [values, setValues] = useState({
    name: "",
    phone: "",
    addr: "",
    SNS: "",
    position: "",
    email: "",
    time: "",
    memo: "",
  });
  console.log(existingUser[0]);

  const handleEditUser = () => {
    setValues({
      name: "",
      phone: "",
      addr: "",
      SNS: "",
      position: "",
      email: "",
      time: "",
      memo: "",
    });
    dispatch(
      viewCard({
        id: params.id,
        name: values.name,
        phone: values.phone,
        addr: values.addr,
        SNS: values.SNS,
        position: values.position,
        email: values.email,
        memo: values.memo,
      })
    );
    navigate("/userlist");
  };

  return (
    <div className="h-screen">
      <div className="mt-10 w-1/3 m-auto">
        <div className="flex flex-col">
          <div className ="mb-2 text-base font-bold text-gray-800">
    


          </div>
          <h1 className= "py-2 mb-2 px-3 border-2 outline-none">{name}</h1>

          <TextField
            label="이름"
            value={values.name}
            inputProps={{ type: "text", placeholder: name }}
          />
          <TextField
            label="전화번호"
            value={values.phone}
            placeholder={phone}
          />
          <TextField
            label="주소"
            value={values.addr}
            inputProps={{ type: "text", placeholder: addr }}
          />
          <TextField
            label="SNS"
            value={values.SNS}
            inputProps={{ type: "text", placeholder: SNS }}
          />
          <TextField
            label="직책/직무"
            value={values.position}
            inputProps={{ type: "text", placeholder: position }}
          />
          <TextField
            label="Email"
            value={values.email}
            inputProps={{ type: "email", placeholder: email }}
          />
          <TextField
            label="메모"
            value={values.memo}
            inputProps={{
              type: "text",
              placeholder: memo,
            }}
          />
        </div>
        <br />

        <div className="justify-between flex">
          <Button onClick={() => navigate("/userlist")}>이전</Button>
          <Button onClick={handleEditUser}>수정</Button>
        </div>
      </div>
    </div>
  );
};

export default ViewCard;
