import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import Button from "../../components/Button";
import { deleteUser } from "./userSlice";
import { useNavigate, useParams } from "react-router-dom";
const Confirm = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const users = useSelector((store) => store.users);
  const handleRemoveUser = (id) => {
    dispatch(deleteUser({ id }));
  };

  const renderCard = () =>
    users.map((user) => (
      <div
        className="w-full h-48 bg-gray-200 p-5 flex items-center justify-between"
        key={user.id}
      >
        <div>
          <img
            className="h-24 w-auto rounded-md"
            alt="업로드 이미지"
            src={user.image}
          ></img>

          <h3 className="font-bold text-lg text- text-gray-700">{user.name}</h3>
          <p className="font-normal text-gray-600">{user.memo}</p>
        </div>
        <div className="flex gap-4"></div>
      </div>
    ));

  return (
    <div>
      <div className="text-center"></div>
      <div className="h-screen">{renderCard()}</div>
      <div className = "justify-between flex">
      <Button onClick={() => navigate("/view-templates")}>이전</Button>
      <Button onClick={() => navigate("/order")}>결제</Button>
      </div>
    </div>
  );
};

export default Confirm;
